mapper.select:
select clue.id, clue.fullname, clue.company, clue.phone, clue.mphone, clue.source, clue.appellation, user.name as owner, clue.state from tbl_clue clue left join tbl_user user on clue.owner=user.id
and clue.fullname like '%' #{fullname} '%'
and clue.company like '%' #{company} '%'
and clue.phone like '%' #{phone} '%'
and user.name like '%' #{owner} '%'
and clue.mphone like '%' #{mphone} '%'
and clue.source = #{mphone}
and clue.state = #{state}
order by clue.create_time desc limit #{beginNo},#{pageSize}

mapper.delete:
delete from tbl_clue
where id in

  #{id}
